import Image from "next/image";
import React from "react";
import { cn } from "@/lib/utils";

interface ImageWrapperProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  fill?: boolean;
  className?: string;
  priority?: boolean;
  sizes?: string;
  objectFit?: "contain" | "cover" | "fill" | "none" | "scale-down";
}

const objectFitClasses = {
  contain: "object-contain",
  cover: "object-cover",
  fill: "object-fill",
  none: "object-none",
  "scale-down": "object-scale-down",
};

const ImageWrapper: React.FC<ImageWrapperProps> = ({
  src,
  alt,
  width,
  height,
  fill = false,
  className,
  priority = false,
  sizes,
  objectFit = "cover",
}) => {
  if (fill) {
    return (
      <Image
        src={src}
        alt={alt}
        fill
        className={cn(objectFitClasses[objectFit], className)}
        sizes={sizes || "100vw"}
        priority={priority}
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
      />
    );
  }

  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      className={cn(objectFitClasses[objectFit], className)}
      sizes={sizes}
      priority={priority}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  );
};

export default ImageWrapper;
